const products = [
    { id: '1', name: 'iPhone 16', price: 1500000, category: 'iPhone', img: 'data:image/jpeg;base64,', stock: 30, description: 'iPhone 16 Blue Marine 128 GB' },
    { id: '2', name: 'iPhone 16 Pro', price: 2200000, category: 'iPhone', img: 'data:image/jpeg;base64,', stock: 40, description: 'iPhone 16 Pro Titanium White' },
    { id: '3', name: 'iPhone 16 Pro Max', price: 2500000, category: 'iPhone', img: 'data:image/jpeg;base64,', stock: 50, description: 'iPhone 16 Pro Max 256 GB' },
    { id: '4', name: 'Macbook Air M3', price: 1500000, category: 'Macs', img: 'data:image/jpeg;base64,' , stock: 80}
];

export const getProducts = () => {
    return new Promise((resolve) => {
        setTimeout(() => {
            console.log("Products:", products);
            resolve(products);
        }, 500);
    });
};

export const getProductsByCategory = (categoryId) => {
    return new Promise((resolve) => {
        setTimeout(() => {
            const filteredProducts = products.filter(product => product.category === categoryId);
            console.log("Filtered Products:", filteredProducts);
            resolve(filteredProducts);
        }, 500);
    });
};

export const getProductByID = (id) => {
    return new Promise((resolve) => {
        setTimeout(() => {
            const product = products.find(product => product.id === id);
            console.log("Product by ID:", product);
            resolve(product);
        }, 500);
    });
};
